SetVar(range_r, Calculate(Exp: #current_page# + 6, Type: int))
SetVar(range_l_max, Calculate(Exp: #last_page# - #pager_limit#, Type: int))
SetVar(pager_limit_plus_one, Calculate(Exp: #pager_limit# + 1, Type: int))

If(#current_page# < 5){
