        Range(src_pages, 1, 11)
    }.Else{
        Range(src_pages, 1, #last_page_plus_one#) 
    }
}.ElseIf(#delta_last_page# < 6){
    If(#range_l_max# > 0){
        Range(src_pages, #range_l_max#, #last_page_plus_one#)
    }.Else{
        Range(src_pages, 1, #last_page_plus_one#)
    }
}.Else{
    Range(src_pages, #range_l#, #range_r#)
}


