contract DelApplication {
    data {
        ApplicationId int
        Value int "optional"
    }

    conditions {
        RowConditions("applications", $ApplicationId, false)
    }

    action {
        if $Value == 1 {
            DBUpdate("applications", $ApplicationId, "deleted", 1)
        }
        else {
            DBUpdate("applications", $ApplicationId, "deleted", 0)
        } 
    }
}

contract EditAppParam {
	data {
		Id int
		Value string
		Conditions string
	}
	func onlyConditions() bool {
		return $Conditions && !$Value
	}
	
	conditions {
		RowConditions("app_params", $Id, onlyConditions())
		ValidateCondition($Conditions, $ecosystem_id)
	}
	
	action {
		DBUpdate("app_params", $Id, "value,conditions", $Value, $Conditions )
	}
}

contract EditApplication {
    data {
        ApplicationId int
        Conditions string
    }

    conditions {
		ValidateCondition($Conditions, $ecosystem_id)
		RowConditions("applications", $ApplicationId, false)
    }

    action {
        DBUpdate("applications", $ApplicationId, "conditions", $Conditions)
    }
}

contract EditColumn {
	data {
		TableName string
		Name string
		Permissions string
	}

	conditions {
		ColumnCondition($TableName, $Name, "", $Permissions)
	}

	action {
		PermColumn($TableName, $Name, $Permissions)
	}
}

contract EditLang {
	data {
        Id int
		Trans string "optional"
		Value array "optional"
		IdLanguage array "optional"
        ApplicationId int "optional"
	}
	
	conditions {
		if (Len($IdLanguage) > 0){
			var j,len int
			len = Len($IdLanguage)
			while j < len {
				if ($IdLanguage[j] == ""){
					info("Locale empty")
				}
				if ($Value[j] == ""){
					info("Value empty")
				}
				j = j + 1
			}
		}
		EvalCondition("parameters", "changing_language", "value")
	}
	
	action {
		if (Len($IdLanguage) > 0){
			var i,len,lenshar int
			var res,langarr string
			len = Len($IdLanguage)
			lenshar = Len($Value)
			while i < len {
				if (i + 1 == len){
					res = res + Sprintf(`%q: %q`,$IdLanguage[i],$Value[i])
					i = i + 1
				}
				else {
					res = res + Sprintf(`%q: %q, `,$IdLanguage[i],$Value[i])
					i = i + 1
				}
			}
			langarr = Sprintf("{"+"%v"+"}", res)
			$Trans = langarr
            DBUpdate("languages",$Id,"res",$Trans)
			//UpdateLang($Id, $Trans)
		}
		else {
			DBUpdate("languages",$Id,"res",$Trans)
			//UpdateLang($Id, $Trans)
		}
	}
}

