contract voting_TemplateRun{
    data{
        TableWritten string "optional"
        RowId string "optional"
        ColumnName string "optional"
        NewValue string "optional"
        TextDocument string "optional"
        KeyId int "optional"
        TemplateId int
        Start string "date"
        StartTime string "date,optional"
        End string "date"
        EndTime string "date,optional"
    }
    func checkInput(value, name string){
        if Size(value) == 0 || value == "NULL" {
            warning Sprintf("%q not found", name)
        }
    }

    conditions{
        $template = DBFind("voting_templates").WhereId($TemplateId).Row()
        if !$template{
            warning "Voting template not found"
        }
        if $KeyId != 0{
            $keyFind = DBFind("keys").Where("id=?", $KeyId).Row()
            if !$keyFind{
                warning "Key id not found"
            }
        }
        if $StartTime == ""{
            $StartTime = "00:00"
        }
        if $EndTime == ""{
            $EndTime = "23:59"
        }

        $initContract = $template["init_contract"]
        if $initContract != "" && $initContract != "NULL"{
            var initParam map
            initParam["KeyId"] = $KeyId
            CallContract($condition, initParam)
        }

        $voters = $template["voters"]
        $votingName = $template["title"]
        $volume = Int($template["volume"])
        $quorum = Int($template["quorum"])
        $voting = Int($template["type_voting"])
        $participants = Int($template["type_participants"])
        $decision = Int($template["type_decision"])
        $vacancies = Int($template["vacancies"])

        $desc = Sprintf("%v [template #%v]", $votingName, $TemplateId)

        $contractAccept = $template["contract_accept"]
        if $contractAccept == "NULL"{
            $contractAccept = ""
        }
        $acceptParams = $template["accept_params"]
        if $acceptParams == "NULL"{
            $acceptParams = ""
        }

        $contractReject = $template["contract_reject"]
        if $contractReject == "NULL"{
            $contractReject = ""
        }
        $rejectParams = $template["reject_params"]
        if $rejectParams == "NULL"{
            $rejectParams = ""
        }

        if $decision == 3 && {
            checkInput($TextDocument, "TextDocument")
        }
        if $decision == 4 {
            checkInput($TableWritten, "TableWritten")
            checkInput($RowId, "RowId")
            checkInput($ColumnName, "ColumnName")
            checkInput($NewValue, "NewValue")
        }
        $isCreated = DBFind("voting_instances").Where("name=? and description=? and delete=0", $votingName, $desc).Row()
        if $isCreated{
            // info "Voting already created" // проверка дубликатов голосования
        }

        if $participants == 3 {
            // если тип участников "по роли" - проверяю роль
            var role map
            role = DBFind("roles_list").Where("id=? and delete=0", $voters).Row()
            if !role {
                warning Sprintf("Voters (%v) not found", role["role_name"])
            }
            $roleId = Int(role["id"])
        }
    }

    action{

        $voting_id = 0 // должно установиться в voting_CreateNew
        voting_CreateNew("voting_name,type_voting,description,type_participants,type_decision,now_date,start_time,start_date,end_date,end_time,volume,quorum", $votingName, $voting, $desc, $participants, $decision, $Start, $StartTime, $Start, $End, $EndTime, $volume, $quorum)
        if $voting_id == 0 {
            // если не смогло установиться в voting_CreateNew (фикс глобальных переменных)
            $voting_id = DBFind("voting_instances").Where("voting_name=? and start_date=?", $votingName, $Start).One("id")
            $voting_id = Int($voting_id)
        }
        $defaultParams = Sprintf("VotingTemplateId->%v;KeyId->%v;VotingId->%v", $TemplateId, $KeyId, $voting_id)
        if Size($acceptParams) == 0{
            $acceptParams = $defaultParams
        }
        if Size($rejectParams) == 0{
            $rejectParams = $defaultParams
        }

        if $decision == 1 || $decision == 2{
            // 1. Ролевые кандидаты с регистрацией участников по запросу
            // 2. Ролевые кандидаты с ручной регистрацией участников
            voting_SubjectCandidates("votingID,memberID", $voting_id, $KeyId)
            voting_SubjectSettings("votingID,roleID,vacancies,contract_accept,accept_params,contract_reject,reject_params", $voting_id, $roleId, $vacancies, $contractAccept, $acceptParams, $contractReject,$rejectParams)
        }
        if $decision == 3 {
            // Утверждение документа
            voting_SubjectDocument("votingID,text_document,contract_accept,contract_accept_params,contract_reject,contract_reject_params", $voting_id,$TextDocument, $contractAccept, $acceptParams, $contractReject, $rejectParams)
        }

        if $decision == 4 {
            // Формальное решение
            voting_SubjectFormal("votingID,description,table_to_written,row_id,column_name,written_value,contract_accept,contract_accept_params,contract_reject,contract_reject_params",$voting_id, $desc, $TableWritten, $RowId, $ColumnName, $NewValue, $contractAccept, $acceptParams, $contractReject, $rejectParams)

        }

        if $decision == 5 {
            // Выполнение контракта
            voting_SubjectContract("votingID,contract_accept,contract_accept_params,contract_reject,contract_reject_params",$voting_id,$contractAccept, $acceptParams, $contractReject, $rejectParams)
        }

        voting_Invite("votingID,var_id", $voting_id, $roleId)
    }
}

contract voting_TemplateStore{
    data{
        Title string
        TypeParticipants int
        TypeVoting int
        TypeDecision int
        Volume int
        Quorum int
        Subject string
        Voters string "optional"
        InitContract string "optional" // Выполнить перед голосованием
        Vacancies int "optional"
        ContractAccept string "optional"
        ParamsAccept string "optional"
        ContractReject string "optional"
        ParamsReject string "optional"
    }

    func conditions{
        if $TypeDecision == 1 || $TypeDecision == 2 || $TypeParticipants == 3 {
            if $Voters == 0 {
                warning "Voters role not found"
            }
        }
        if Size($Title) <3{
            warning "Please enter title"
        }
        if $Volume <0 || $Volume > 100{
            warning "Wrong volume range"
        }
        if $Quorum <0 || $Quorum > 100{
            warning "Wrong quorum range"
        }

        if $Vacancies < 1 && ($TypeDecision == 1 || $TypeDecision == 2) {
            warning "wrong number vacancies"
        }

        if Size($InitContract) > 0{
            if GetContractByName($InitContract) == 0 {
                warning "Init Contract not found"
            }
        }
        if Size($ContractAccept) > 0{
            if GetContractByName($ContractAccept) == 0 {
                warning "Contract for accepted decision not found"
            }
        }
        if Size($ContractReject) > 0{
            if GetContractByName($ContractReject) == 0{
                warning "Contract for rejected decision not found"
            }
        }
    }

    func action{
        DBInsert("voting_templates", "title,subject,voters,init_contract,volume,quorum,type_voting,type_participants,type_decision,vacancies,contract_accept,accept_params,contract_reject,reject_params", $Title, $Subject, $Voters, $InitContract, $Volume, $Quorum, $TypeVoting, $TypeParticipants, $TypeDecision, $Vacancies, $ContractAccept, $ParamsAccept, $ContractReject, $ParamsReject)

    }
}

