contract membership_accept {
    data {
        request_id int
        notific_id int
    }
    conditions {

    }
    action {
        Notifications_Single_Close("notific_id", $notific_id)

        var request map
        request = DBFind("membership_requests").Where("id = ?", $request_id).Row()
        DBUpdate("membership_requests", $request_id, "request_status", 1)

        var params map
        params["request_id"] = $request_id

        Notifications_Single_Send_map("member_id,sender,icon_name,text_header,text_body,page_name,params_map", request["member_id"], $key_id, "icon icon-user-follow", "Request is accepted", "Your request for membership is accepted", "membership_user_view", params)
    }
}

contract membership_decline {
    data {
        request_id int
        notific_id int
    }
    conditions {

    }
    action {
        Notifications_Single_Close("notific_id", $notific_id)

        var request map
        request = DBFind("membership_requests").Where("id = ?", $request_id).Row()
        DBUpdate("membership_requests", $request_id, "request_status", -1)

        var params map
        params["request_id"] = $request_id

        Notifications_Single_Send_map("member_id,sender,icon_name,text_header,text_body,page_name,params_map", request["member_id"], $key_id, "icon icon-user-follow", "Request is rejected", "Your request for membership is rejected", "membership_user_view", params)
    }
}

contract membership_send_request {
    data {
        ecosystem_num int
    }
    
    conditions {
        var found int
        found = DBFind("membership_requests").Where("member_id = ? and ecosystem_id = ? and request_status = 0", $key_id, $ecosystem_num)
        if Len(found) > 0 {
            warning "Request already sent."
        }
    }
    
    action {
        
        var request_id int
        request_id = DBInsert("membership_requests", "member_id,ecosystem_id", $key_id, $ecosystem_num)

        var admin_json string
        admin_json = DBFind("roles_participants").Where("role->id = ? and deleted = 0", 1).Ecosystem($ecosystem_num).One("member")

        var admin map
        admin = JSONToMap(admin_json)

        var req_params map
        req_params["ecosystem_num"] = $ecosystem_num

        var notific_params map
        notific_params["request_id"] = request_id
        
        
        
        Notifications_Single_Send_map("member_id,sender,icon_name,text_header,text_body,page_name,params_map", admin["member_id"], 1, "icon icon-user-follow", "New request for membership", "View details", "membership_admin_view", notific_params)
    }
}

Div(content-wrapper){
    SetTitle(Membership Request)
	
	DBFind(Name: notifications, Source: src_notific).Columns("page_params->request_id,sender->image_id,sender->member_id,sender->member_name").Where("id = #notific_id#").Vars(notific)
    
    If(#prefix_image_id#>0){
        DBFind(Name: binaries, Source: src_binaries).Columns("id,data").Where("id=#notific_sender_image_id#").Vars(img)
    }
    
    Div(row){
        Div(col-sm-8 col-sm-offset-2 col-lg-6 col-lg-offset-3){
            Form(panel panel-default){
                Div(panel-heading text-center){
                    Span(Class: h3, Body: Membership Request)
                }
                Div(panel-body){
                    Div(form-group){
                        Div(row){
                            Div(col-md-12 mt-sm text-center){
								If(#img_id#>0){
									Image(Src: "#img_data#", Class: img-circle).Style(width: 80px; border: 1px solid #5A5D63;)
								}.Else{
									Span(Class: fa icon-user fa-5x)
								}
								Div(m0 h3 text-bold){#notific_sender_member_name#}
							
                            }
                        }
                    }
                    Div(list-group-item text-center){
                        Div(text-muted m0 h5, Body: LangRes(address))
                        Span(Class: h5 text-bold, Body: Address(#notific_sender_member_id#))
                    }
					Div(list-group-item text-center){
                        Button(Class: btn btn-success, Body: Accept, Contract: "membership_accept", Page: default_page, Params: "request_id=#notific_page_params_request_id#,notific_id=#notific_id#")
                        Button(Class: btn btn-danger, Body: Reject, Contract: "membership_decline", Page: default_page, Params: "request_id=#notific_page_params_request_id#,notific_id=#notific_id#")
                    }
                }
            }
        }
    }
}

Div(content-wrapper){
