contract Notifications_Roles_Close {
    data {
        notific_id int
    }

    conditions {
        $notifications_map = DBFind("notifications").Columns("id,date_start_processing,processing_info->member_id,closed").Where("id=$", $notific_id).Row()
        if !$notifications_map {
            warning "Notification not found"
        }

        if $notifications_map["date_start_processing"] == "" {
            warning "Sorry, processing of this notification has not yet begun"
        }

        if $notifications_map["processing_info.member_id"] != $key_id {
            warning "Sorry, processing of this notice began another member"
        }

        if $notifications_map["closed"] > 0 {
            warning "Sorry, this notification has already been closed before"
        }
    }

    action {
        DBUpdate("notifications", $notific_id, "timestamp date_closed,closed", $block_time, 1)
    }
}

contract Notifications_Roles_Processing {
    data {
        notific_id int
    }

    conditions {
        $notifications_map = DBFind("notifications").Where("id=$", $notific_id).Row()
        if !$notifications_map {
            warning "Notification not found"
        }

        if $notifications_map["date_start_processing"] != "" {
            warning "Sorry, processing of this notification has already begun"
        }

        $participant_id = DBFind("roles_participants").Where("role->id=$ and member->member_id=$ and deleted=0", Int($notifications_map["role_id"]), $key_id).One("id")
        if $participant_id == 0 {
            warning "Sorry, you are not part of the role for which this notification is intended"
        }

        $processing_map = DBFind("members").Where("id = $", $key_id).Row()
        if !$processing_map {
            warning "Member not found"
        }
    }

    action {
        var processing_info map
        processing_info["member_id"] = $processing_map["id"]
        processing_info["member_name"] = $processing_map["member_name"]
        processing_info["image_id"] = $processing_map["image_id"]

        DBUpdate("notifications", $notific_id, "timestamp date_start_processing,processing_info", $block_time, processing_info)
    }
}

contract Notifications_Single_Send_map {
    data {
        member_id int
        sender int
        icon_name string
        text_header string
        text_body string "optional"
        page_name string
        params_map map
    }

    conditions {
        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        if !$member_map {
            warning "Member not found"
        }

        if $sender == 1 {
            $sender_map = DBFind("members").Where("id = $", $key_id).Row()
            if !$sender_map {
                warning "Member not found"
            }
        }
        if $sender == 2 {
            // !!! need to fix role_id to the global variable !!!!
            $sender_map = DBFind("roles").Where("id = $", 1).Row()
            if !$sender_map {
                warning "Member not found"
            }
        }
    }

    action {
        var recipient map
        recipient["member_id"] = $member_map["id"]
        recipient["member_name"] = $member_map["member_name"]
        recipient["image_id"] = $member_map["image_id"]

        var sender map
        if $sender == 1 {
            sender["type"] = 1
            sender["member_id"] = $sender_map["id"]
            sender["member_name"] = $sender_map["member_name"]
            sender["image_id"] = $sender_map["image_id"]
        }
        if $sender == 2 {
            sender["type"] = 2
            sender["role_id"] = $sender_map["id"]
            sender["role_name"] = $sender_map["role_name"]
            sender["image_id"] = $sender_map["image_id"]
        }

        var notification map
        notification["header"] = $text_header
        notification["type"] = 1
        notification["icon"] = $icon_name
        notification["body"] = $text_body

        DBInsert("notifications", "recipient,sender,notification,page_name,page_params,timestamp date_created",
            recipient, sender, notification, $page_name, $params_map, $block_time)
    }
}

