contract Roles_Unassign {
    data {
        row_id int
    }

    conditions {
        // check record
        var assigned, role map
        assigned = DBFind("roles_participants").Columns("id,role->id").Where("id=$", $row_id).Row()
        if !assigned {
            warning "Participant not found"
        }

        role = DBFind("roles").Columns("id,creator->member_id").Where("id=?", assigned["role.id"]).Row()
        if !role {
            warning "Role not found in the roles registry"
        }

        var specialRules map
        specialRules["sysparams_AcceptValidator"] = "Validator"
        specialRules["sysparams_DelegateRefresh"] = "Delegate"
        specialRules["sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["sysparams_InvestorRefresh"] = "Investor with voting rights"
        specialRules["sysparams_ValidatorResign"] = "Validator"

        if !(specialRules[$original_contract] == role["role_name"]) {
            // check creator of the role
            if role["creator.member_id"] != $key_id {
                warning "Sorry, you are not the creator of this role"
            }
        }
    }

    action {
        // delete the member
        DBUpdate("roles_participants", $row_id, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract UploadBinary {
    data {
        Name string
        Data string
        AppID int
        MemberID int "optional"
    }
    conditions {
        $Id = Int(DBFind("binaries").Columns("id").Where("app_id = ? AND member_id = ? AND name = ?", $AppID, $MemberID, $Name).One("id"))
    }
    action {
        var hash string
        hash = MD5($Data)

        var imageId int
        if $Id != 0 {
             DBUpdate("binaries", $Id, "data,hash", $Data, hash)
             imageId = $Id
        } else {
             imageId = DBInsert("binaries", "app_id,member_id,name,data,hash", $AppID, $MemberID, $Name, $Data, hash)
        }
        $result =  imageId
    }
}

contract Roles_Assign {
    data {
        role_id int
        member_id int
    }

    conditions {
        $role_map = DBFind("roles").Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Where("id = $", $role_id).Row()
        if !$role_map {
            warning "Role not found"
        }

        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        if !$member_map {
            warning "Member not found"
        }

        $appointer_map = DBFind("members").Where("id = $", $key_id).Row()
        if !$appointer_map {
            warning "Appointer not found"
        }

        if $role_map["deleted"] == 1 {
            warning "This role has been deleted. The appointment of members is impossible"
        }

        var specialRules map
        specialRules["sysparams_AcceptValidator"] = "Validator"
        specialRules["sysparams_DelegateRefresh"] = "Delegate"
        specialRules["sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["sysparams_InvestorRefresh"] = "Investor with voting rights"

        if !(specialRules[$original_contract] == $role_map["role_name"]) {
            if $role_map["role_type"] != 1 && $role_map["role_type"] != 4 {
                warning "This role is not assignable. The appointment of members is impossible"
            }
            // check creator of the role
            if $role_map["creator.member_id"] != $key_id {
                warning "Sorry, you are not the creator of this role. The appointment of members is impossible"
            }
        }

        $assign_id = DBFind("roles_participants").Where("role->id=$ and member->member_id=$ and deleted=0", $role_id, $member_id).One("id")
        if $assign_id {
            warning "This member has already been added"
        }

    }

    action {
        var role map
        role["id"] = $role_map["id"]
        role["type"] = $role_map["role_type"]
        role["name"] = $role_map["role_name"]
        role["image_id"] = $role_map["image_id"]

        var member map
        member["member_id"] = $member_map["id"]
        member["member_name"] = $member_map["member_name"]
        member["image_id"] = $member_map["image_id"]

        var appointed map
        appointed["member_id"] = $appointer_map["id"]
        appointed["member_name"] = $appointer_map["member_name"]
        appointed["image_id"] = $appointer_map["image_id"]

        DBInsert("roles_participants", "role,member,appointed,timestamp date_created", role, member, appointed, $block_time)
    }
}

contract sysparams_RemoveNode{
    data{}
    func jsonToArray(json string) array {
        // KOSTYL
        var res, parts array
        if Size(json) < 60 {
            // fastway empty|incorrect detect
            return res
        }
        if HasPrefix(json, "[") {
            json = Substr(json, 1, Size(json)-2)
            if Contains(json, "},"){
                // json have many objects
                parts = Split(json, "},")
                var i int
                while i < Len(parts){
                    var p, lastSym string
                    p = parts[i]
                    lastSym = Substr(p, Size(p)-1, 1)
                    if lastSym != "}"{
                        p = Sprintf("%v}", p)
                    }
                    res[i] = JSONToMap(p)
                    i=i+1
                }
            }else{
                if HasPrefix(json, "{"){
                    res[0] = JSONToMap(json)
                }
            }
        }
        return res
    }
    func nodesRemove(keyId int) array{
        var json string
        var nodes, res array
        json = SysParamString("full_nodes")
        if !HasPrefix(json, "[") {
            // empty string nodes
            return res
        }
        nodes = jsonToArray(json)
        var i, i_res int
        while i < Len(nodes){
            var node map
            node = nodes[i]
            if node["key_id"] != keyId {
                res[i_res] = node
                i_res = i_res+1
            }
            i=i+1
        }
        return res
    }
    func nodesArrayToJson(nodes array) string{
        var res string
        var json array
        if Len(nodes) == 0 {
            res = "[]"
            return res
        }
        var i int
        while i < Len(nodes){
            var node map
            node = nodes[i]
            json[i] = Sprintf(`{"tcp_address":"%v", "api_address":"%v", "key_id":"%v", "public_key":"%v"}`, node["tcp_address"], node["api_address"], node["key_id"], node["public_key"])
            i=i+1
        }
        res = Join(json, ",")
        res = Sprintf("[%v]", res)
        return res
    }
    conditions{
        if $original_contract != "sysparams_ValidatorResign"{
            warning "Unknown contract"
        }
    }

    action{
        var nodes array
        nodes = nodesRemove($key_id)
        var fullNodes string
        fullNodes = nodesArrayToJson(nodes)
        DBUpdateSysParam("full_nodes", fullNodes, "")
    }
}

contract TokenTransfer {
    data {
        Amount money
        Sender_AccountId int
        Recipient_AccountId int
        Comment string "optional"
        Signature string "optional hidden"
    }

    conditions {
        //check amount
        if $Amount == 0 {
            error "Amount is zero"
        }
        if $Amount < 0 {
            error "Amount is negative"
        }

        //check the sender
        var sender string
        sender = DBFind("keys").Where("id=$", $Sender_AccountId).One("id")
        if !sender {
            error Sprintf("Sender %v is invalid", $Sender_AccountId)
        }

        //check balance
        $sender_balance = Money(DBFind("keys").Where("id=$", $Sender_AccountId).One("amount"))
        if $Amount > $sender_balance {
            error Sprintf("Money is not enough %v < %v", $sender_balance, $Amount)
        }

        //check the recipient
        var recipient string
        recipient = DBFind("keys").Where("id=$", $Recipient_AccountId).One("id")
        if !recipient {
            error Sprintf("Recipient %v is invalid", $Recipient_AccountId)
        }
    }

    action {
        DBUpdate("keys", $Sender_AccountId, "-amount", $Amount)
        DBUpdate("keys", $Recipient_AccountId, "+amount", $Amount)
        DBInsert("history", "sender_id,recipient_id,amount,comment,block_id,txhash", $Sender_AccountId, $Recipient_AccountId, $Amount, $Comment, $block, $txhash)
    }
}

