contract Roles_Create {
    data {
        role_name string
        role_type int
        icon string "image,optional"
        company_id int "optional"
    }

    conditions {
        // system role
        if $role_type == 3 {
            warning "System role can not be created"
        }

        // company role
        if $role_type == 4 {
            // check param
            if $company_id == 0 {
                warning "On this page you can not create a role for the company. Go to the company's view page to create a role"
            }

            // check creator of the company
            if $company_id != $key_id {
                warning "Sorry, you are not the creator of this company"
            }
        }

        $member_map = DBFind("members").Where("id = $", $key_id).Row()
        if !$member_map {
            warning "You are not a member of the ecosystem"
        }
    }

    action {
        var creator map
        creator["member_id"] = $member_map["id"]
        creator["member_name"] = $member_map["member_name"]
        creator["image_id"] = $member_map["image_id"]

        var imageId int
        if Size($icon) > 0 {
            // binaries index: app_id + member_id + name
            // TODO: GET app_id for real applications id. It is zero for debug
            var appId, memberId int
            appId = 0
            memberId = $key_id
            imageId = UploadBinary("Name,Data,AppID,MemberID", $role_name, $icon, appId, memberId)
        }

        // insert role
        if $role_type == 4 {
            $result = DBInsert("roles", "role_name,role_type,image_id,creator,timestamp date_created,company_id",
            $role_name, $role_type, imageId, creator, $block_time, $company_id)
        } else {
            $result = DBInsert("roles", "role_name,role_type,image_id,creator,timestamp date_created,company_id",
            $role_name, $role_type, imageId, creator, $block_time, 0)
        }
    }
}

contract catalog_ecosystems_Add {
    data {
        Ecosystem_name string
        Ecosystem_number int
        Logo string "image"
        Ecosystem_type int
        Description string
        Conditions string
        Flag_free int
        Vde_form_address string "optional"
        Vde_page string "optional"
        Web_form_address string "optional"
        Web_page string "optional"
        Email string "optional"
        Phone string "optional"
    }
    conditions {
        var FOUNDER_ACCOUNT, DELEGATE_ACCOUNT string
        FOUNDER_ACCOUNT = "founder_account"
        DELEGATE_ACCOUNT = "delegate_account"

        if Size($Email) > 0 {
            var emailParts int
            emailParts = Split($Email, "@")
            if Len(emailParts) != 2 {
                warning "Invalid email"
            }
        }

        if Size($Logo) == 0 {
            warning "Required logo"
        }
        var ecoExists map
        ecoExists = DBFind("catalog_ecosystems").Where("ecosystem_number=? and deleted=0", $Ecosystem_number).Row()
        if ecoExists {
            info "The ecosystem with same number already added"
        }

        var isFounder, isDelegate bool
        var founder, delegate map
        founder = DBFind("parameters").Where("name=?", FOUNDER_ACCOUNT).Ecosystem($Ecosystem_number).Row()
        isFounder = founder["value"] == $key_id

        delegate = DBFind("parameters").Where("name=?", DELEGATE_ACCOUNT).Ecosystem($Ecosystem_number).Row()
        if delegate {
            isDelegate = delegate["value"] == $key_id
        }
        if !(isFounder || isDelegate) {
            warning "Sorry, you can not add this ecosystem"
        }
    }

    action {
        var ecosystem_info map
        ecosystem_info["description"] = $Description
        ecosystem_info["conditions"] = $Conditions
        ecosystem_info["email"] = $Email
        ecosystem_info["phone"] = $Phone

        var url_address map
        url_address["web_page"] = $Web_page
        url_address["web_form_address"] = $Web_form_address
        url_address["vde_page"] = $Vde_page
        url_address["vde_form_address"] = $Vde_form_address

        var image_id int
        image_id = DBInsert("binaries", "data", $Logo)

        DBInsert("catalog_ecosystems", "ecosystem_name,ecosystem_number,ecosystem_type,image_id,ecosystem_info,url_address,flag_free",
        $Ecosystem_name, $Ecosystem_number, $Ecosystem_type, image_id, ecosystem_info, url_address, $Flag_free)
    }
}

