contract voting_TemplateRun{
    data{
        TemplateId int
        Start string "date"
        End string "date"

        EndTime string "date,optional"
        StartTime string "date,optional"
        KeyId int "optional"
        TableWritten string "optional"
        RowId string "optional"
        ColumnName string "optional"
        NewValue string "optional"
        TextDocument string "optional"
    }
    func checkInput(value, name string){
        if Size(value) == 0 || value == "NULL" {
            warning Sprintf("%q not found", name)
        }
    }
    func emptyOrValue(value string) string{
        if value == "NULL" {
            value = ""
        }
        return value
    }

    conditions{
        var template map
        template = DBFind("voting_templates").Where("id=?",$TemplateId).Row()
        if !template{
            warning "Voting template not found"
        }
        if $KeyId != 0{
            $keyFind = DBFind("keys").Where("id=?", $KeyId).Row()
            if !$keyFind{
                warning "Key id not found"
            }
        }
        if $StartTime == ""{
            $StartTime = "00:00"
        }
        if $EndTime == ""{
            $EndTime = "23:59"
        }

        var initContract string
        initContract = template["init_contract"]
        if initContract != "" && initContract != "NULL"{
            var initParam map
            initParam["KeyId"] = $KeyId
            CallContract(initContract, initParam)
        }
        $voters = template["voters"]
        $candidates = template["candidates"]
        $votingName = template["title"]
        $volume = Int(template["volume"])
        $quorum = Int(template["quorum"])
        $type_voting = Int(template["type_voting"])
        $participants = Int(template["type_participants"])
        $decision = Int(template["type_decision"])
        $vacancies = Int(template["vacancies"])

        $desc = Sprintf("%v template #%v", $votingName, $TemplateId)
        if $type_voting < 1 || $type_voting > 2 {
            warning "Invalid type of voting"
        }
        $contractAccept = emptyOrValue(template["contract_accept"])
        $acceptParams = emptyOrValue(template["accept_params"])
        $contractReject = emptyOrValue(template["contract_reject"])
        $rejectParams = emptyOrValue(template["reject_params"])

        if $decision == 1 || $decision == 2 {
            // role-candidates
            checkInput($candidates, "Candidate role")
            var candidate map
            candidate = DBFind("roles").Where("id=? and deleted=0", $candidates).Row()
            if !candidate {
                warning Sprintf("Candidates role (id: %v) not found", $candidates)
            }
            $candidateId = Int(candidate["id"])
        }

        if $decision == 3 {
            // document approval
            checkInput($TextDocument, "TextDocument")
        }

        if $decision == 4 {
            // formal decision
            // in the system vote need only a field and a value
            checkInput($ColumnName, "ColumnName")
            checkInput($NewValue, "NewValue")
            if $type_voting == 2{
                $desc = Sprintf("%v [template #%v] %v", $votingName, $TemplateId, $ColumnName)
            }

            if $type_voting == 1{
                checkInput($TableWritten, "TableWritten")
                checkInput($RowId, "RowId")
                $desc = Sprintf("%v [template #%v] %v.%v.%v", $votingName, $TemplateId, $TableWritten, $ColumnName, $RowId)
            }
            if $RowId == "" || $RowId == "NULL"{
                $RowId = 0
            }

        }
        // check duplicated
        // var isCreated map
        // isCreated = DBFind("votings").Where("voting->name=? and voting->description=? and deleted=0", $votingName, $desc).Row()
        // if isCreated{
        //     info "Voting already created"
        // }


        if $participants == 3 {
            // if the type of the participants "in role" - check the role
            var role map
            role = DBFind("roles").Where("id=? and deleted=0", $voters).Row()
            if !role {
                warning Sprintf("Voters role (id: %v) not found", $voters)
            }
            $roleId = Int(role["id"])
        }
    }

    action{
        var votingId int
        // must be installed in voting_CreateNew
        votingId = voting_CreateNew("voting_name,voting_type,description,type_participants,type_decision,now_date,start_time,start_date,end_date,end_time,volume,quorum", $votingName, $type_voting, $desc, $participants, $decision, $Start, $StartTime, $Start, $End, $EndTime, $volume, $quorum)
        if votingId == 0 {
            warning "votingId is zero"
        }
        // the default argument for Contract
        var defaultParams string
        defaultParams = Sprintf(`{"VotingTemplateId":"%v","KeyId":"%v","VotingId":"%v"}`, $TemplateId, $KeyId, votingId)

        if Size($acceptParams) == 0{
            $acceptParams = defaultParams
        }
        if Size($rejectParams) == 0{
            $rejectParams = defaultParams
        }

        if $decision == 1 || $decision == 2{
            // 1. Role candidates with the registration of participants on request
            // 2. Role candidates with manual registration of the participants
            voting_SubjectCandidates("votingID,memberID", votingId, $KeyId)
            voting_SubjectSettings("votingID,roleID,vacancies,contract_accept,contract_accept_params,contract_reject,contract_reject_params", votingId, $candidateId, $vacancies, $contractAccept, $acceptParams, $contractReject,$rejectParams)
        }
        if $decision == 3 {
            // Document approval
            voting_SubjectDocument("votingID,text_document,contract_accept,contract_accept_params,contract_reject,contract_reject_params", votingId,$TextDocument, $contractAccept, $acceptParams, $contractReject, $rejectParams)
        }

        if $decision == 4 {
            // Formal decision
            voting_SubjectFormal("votingID,description,table_to_written,row_id,column_name,written_value,contract_accept,contract_accept_params,contract_reject,contract_reject_params",votingId, $desc, $TableWritten, $RowId, $ColumnName, $NewValue, $contractAccept, $acceptParams, $contractReject, $rejectParams)

        }

        if $decision == 5 {
            // Execution of the contract
            voting_SubjectContract("votingID,contract_accept,contract_accept_params,contract_reject,contract_reject_params",votingId,$contractAccept, $acceptParams, $contractReject, $rejectParams)
        }
        if $participants == 3 {
            // participants: "by role"
            voting_Invite("votingID,var_id", votingId, $roleId)
        }
        if $participants == 1 {
            // if the participants are "all", the invite has already occurred in "voting_CreateNew"
        }
    }
}

contract sysparams_StartNodeAdd{
    data{
        TcpAddress string
        ApiAddress string
        KeyId int
        PubKey string
        TemplateId int
        Start string "date"
        End string "date"
        EndTime string "date"
        StartTime string "date"
    }
    func warn(name, detail string){
        warning Sprintf("%v invalid (%v)", name, detail)
    }
    func checkApiAddress(addr string){
        var name string
        name = "API Address"
        if Size(addr) == 0 {
            warn(name, "size")
        }
        if !(HasPrefix(addr, "http://") || HasPrefix(addr, "https://")) {
            warn(name, "protocol")
        }
        if Contains(addr, " ") {
            warn(name, "space")
        }
        addr = Replace(addr, "http://", "")
        addr = Replace(addr, "https://", "")
        var parts array
        if Contains(addr, ":") {
            parts = Split(addr, ":")
            if Len(parts) > 2 {
                warn(name, ":")
            }
            var port string
            port = parts[1]
            if port <= 0 {
                warn(name, "port")
            }
            addr = parts[0]
        }
    }
    func checkTcpAddress(addr string){
        var name string
        name = "TCP Address"
        if Size(addr) == 0 {
            warn(name, "size")
        }
        if Contains(addr, " ") {
            warn(name, "space")
        }
        var parts array
        if Contains(addr, ":") {
            parts = Split(addr, ":")
            if Len(parts) > 2 {
                warn(name, ":")
            }
            var port string
            port = parts[1]
            if port <= 0 {
                warn(name, "port")
            }
            addr = parts[0]
        }
        parts = Split(addr, ".")
        if Len(parts) != 4 {
            warn(name, "too many parts")
        }
        var i int
        while i < 4 {
            var n int
            n = Int(parts[i])
            if n < 0 || n > 255 {
                warn(name, "part")
            }
            i=i+1
        }
    }
    func checkPub(pub string){
        if PubToID(pub) == 0 {
            warning "Public key invalid"
        }
    }
    func checkKeyId(keyid int){
        var existed map
        existed = DBFind("keys").Where("id=?", keyid).Row()
        if !existed {
            warning "Key id not found"
        }
    }
    func getNodesString() string{
        // kostyl
        // return json-like string from full_nodes trimmed "[" and "]"
        var nodes string
        nodes = SysParamString("full_nodes")
        if HasPrefix(nodes, "[") {
            nodes = Substr(nodes, 1, Size(nodes)-2)
        }
        return nodes
    }
    conditions{
        var id int
        id = Int(EcosysParam("voting_sysparams_template_id"))
        if $TemplateId != id || $TemplateId <= 0 {
            warning "Template id not found"
        }
        checkKeyId($KeyId)
        checkPub($PubKey)
        checkApiAddress($ApiAddress)
        checkTcpAddress($TcpAddress)
    }

    action{
        var sep, nodes, value int
        sep = ","
        nodes = getNodesString()
        if Size(nodes) == 0{
            sep = ""
        }
        value = Sprintf(`{"tcp_address":"%v", "api_address":"%v", "key_id":"%v", "public_key":"%v"}`, $TcpAddress, $ApiAddress, $KeyId, $PubKey)
        nodes = Sprintf("[%v%v%v]", nodes, sep, value)
        voting_TemplateRun("TemplateId,Start,End,EndTime,StartTime,KeyId,TableWritten,RowId,ColumnName,NewValue,TextDocument", $TemplateId, $Start, $End, $EndTime, $StartTime, 0, "", "", "full_nodes", nodes, "")
    }
}

contract voting_TemplateStore{
    data{
        Title string
        TypeParticipants int
        TypeVoting int
        TypeDecision int
        Volume int
        Quorum int
        Subject string
        Voters string "optional" // role of participant
        Candidates string "optional" // role of candidate
        InitContract string "optional" // Execute before the vote
        Vacancies int "optional"
        ContractAccept string "optional"
        ParamsAccept string "optional"
        ContractReject string "optional"
        ParamsReject string "optional"
    }

    func conditions{
        if $TypeParticipants == 3 {
            if Size($Voters) == 0 || $Voters == "NULL" || $Voters == 0 {
                warning "Voters role not found"
            }
        }
        if Size($Title) <3 {
            warning "Please enter title"
        }
        if $Volume <0 || $Volume > 100 {
            warning "Wrong volume range"
        }
        if $Quorum <0 || $Quorum > 100 {
            warning "Wrong quorum range"
        }

        if $TypeDecision == 1 || $TypeDecision == 2 {
            if $Vacancies < 1 {
                warning "Wrong number vacancies"
            }
            if $Candidates == "NULL" || $Candidates == 0 || Size($Candidates) == 0 {
                warning "Wrong candidate role"
            }
        }

        if Size($InitContract) > 0{
            if GetContractByName($InitContract) == 0 {
                warning "Init Contract not found"
            }
        }
        if Size($ContractAccept) > 0{
            if GetContractByName($ContractAccept) == 0 {
                warning "Contract for accepted decision not found"
            }
        }
        if Size($ContractReject) > 0{
            if GetContractByName($ContractReject) == 0{
                warning "Contract for rejected decision not found"
            }
        }
    }

    func action{
        $result = DBInsert("voting_templates", "title,subject,voters,candidates,init_contract,volume,quorum,type_voting,type_participants,type_decision,vacancies,contract_accept,accept_params,contract_reject,reject_params", $Title, $Subject, $Voters, $Candidates, $InitContract, $Volume, $Quorum, $TypeVoting, $TypeParticipants, $TypeDecision, $Vacancies, $ContractAccept, $ParamsAccept, $ContractReject, $ParamsReject)

    }
}

contract voting_RejectDecision {
    data {
        votingID int
    }

    conditions {
        $voting_map = DBFind("votings").Columns("flags->decision,flags->notifics").Where("id=$", $votingID).Row()
        if !$voting_map {
            warning "Voting is not found"
        }

        $participant_id = DBFind("votings_participants").Where("voting_id=$ and member->member_id=$", $votingID, $key_id).One("id")
        if !$participant_id {
            warning "You are not a participant in this voting"
        }
    }

    action {
        if $voting_map["flags.notifics"] == 1 {
            // close sent notifications
            var notific_id string
            notific_id = DBFind("notifications").Where("recipient->member_id=$ and page_name='voting_view' and page_params->voting_id=$ and closed=0", $key_id, $votingID).One("id")
            if notific_id {
                Notifications_Single_Close("notific_id", Int(notific_id))
            }
        }

        if $voting_map["flags.decision"] == 1 {
            warning "Decision was taken. Voting is now not possible"
        }
        var voting_start, voting_end string
        voting_start = DBFind("votings").Where("id=$ and date_started < now()", $votingID).One("id")
        voting_end = DBFind("votings").Where("id=$ and date_ended > now()", $votingID).One("id")
        if !voting_start {
            warning "Voting has not yet begun. Try again later, please"
        }
        if !voting_end {
            warning "Voting has expired. Voting is now not possible"
        }


        DBUpdate("votings_participants", Int($participant_id), "timestamp decision_date,decision", $block_time, -1)

        voting_UpdateDataForGraphs("votingID", $votingID)
    }
}

