						Div(Class: panel-footer clearfix){
							Div(Class: row df f-valign){
								Div(Class: col-md-6 mt-sm text-right){
									Button(Class: btn btn-success, Body: Accept, Contract: car145_acceptApplication, Params: "notific_id=#notific_id#,application_id=#application_id#,stage=#stage#", Page: default_page)
								}
								Div(Class: col-md-6 mt-sm text-left){
									Button(Class: btn btn-danger, Body: Reject, Contract: car145_rejectApplication, Params: "notific_id=#notific_id#,application_id=#application_id#,stage=#stage#,message=#message#", Page: default_page)
								}
							}
						}
					}
                }
            }
        }
        Div(Class: col-md-3)
    }
}

