        $votes_accept = Int($referendums_map["votes_accept"])
        $votes_reject = Int($referendums_map["votes_reject"])
        $percent_accept = Int($referendums_map["percent_accept"])
        $percent_reject = Int($referendums_map["percent_reject"])
