	Div(breadcrumb){
		Span(Class: text-muted, Body: $roles$)
	}

    If(GetVar(isSearch) == 1){
        SetVar(Name: v_Where, Value: "role_name='#v_Search#'")
    }.Else{
        SetVar(Name: v_Where, Value: "id>0")
        SetVar(Name: v_Search, Value: "")
    }

    DBFind(Name: roles_list, Source: src_roles_list).Custom(custom_id){
        If(#delete# == 0){
            Span(Body: #id#)  
        }.Else{
            Span(Class: text-muted, Body: #id#)
        }
    }.Custom(custom_creator){
        If(#role_type#==3){
            Div(){
                Span(Class: h5, Body: LangRes(system_role))
            }
        }.Else{
            If(#delete# == 0){
                LinkPage(Page: profile_view, PageParams: "v_member_id=#creator_id#"){
                    If(#creator_avatar# != ""){
                        Image(Src: "#creator_avatar#", Class: img-circle).Style(width: 30px;  border: 1px solid #5A5D63; margin-right: 10px;)
                    }
                    Span(Class: h5 text-bold, Body: #creator_name#)
                }
            }.Else{
                Div(){
                    If(#creator_avatar# != ""){
                        Image(Src: "#creator_avatar#", Class: img-circle).Style(width: 30px;  border: 1px solid #5A5D63; margin-right: 10px;)
                    }
                    Span(Class: h5 text-muted, Body: #creator_name#)
                }
            }
        }
    }.Custom(custom_name){
        If(#delete# == 0){
            LinkPage(Page: roles_view, PageParams: "v_role_id=#id#,v_role_name=#role_name#"){
                Span(Class: h5 text-bold, Body: LangRes(#role_name#))
            }
        }.Else{
            Span(Class: h5 text-muted, Body: LangRes(#role_name#))
        }
	}.Custom(custom_date){
        If(#delete# == 0){
            Div(Class: h6 m0, Body: DateTime(DateTime: #date_create#, Format: "DD.MM.YYYY HH:MI"))
            Div(Class: h6 m0, Body: DateTime(DateTime: #date_delete#, Format: "DD.MM.YYYY HH:MI"))
        }.Else{
            Div(Class: h6 m0 text-muted, Body: DateTime(DateTime: #date_create#, Format: "DD.MM.YYYY HH:MI"))
            Div(Class: h6 m0 text-muted, Body: DateTime(DateTime: #date_delete#, Format: "DD.MM.YYYY HH:MI"))
        }
	}.Custom(custom_type){
        If(#delete# == 0){
            Span(Body: EcosysParam(Name: roles_types, Index: #role_type#))
        }.Else{
            Span(Class: text-muted, Body: EcosysParam(Name: roles_types, Index: #role_type#))
        }
    }.Custom(custom_status){
        If(#delete# == 0){
            Span(Body: LangRes(active))
        }.Else{
            Span(Class: text-muted, Body: LangRes(deleted))
        }
    }.Custom(actions){
        If(And(#delete#==0,#role_type#!=3)){
            Div(Class: text-center, Body: Button(Body: Em(Class: fa fa-trash), Class: btn btn-danger, Contract: Roles_Del, Params: "role_id=#id#", Page: roles_list))
        }
   }.Where(#v_Where#).Order("delete ASC, id ASC")

    Div(Class: panel panel-primary){
        Form(){
            Div(Class: list-group-item){
                Div(Class: row df f-valign){
                    Div(Class: col-md-1 mt-sm text-right){
                        Label(For: Search){
                            Span(Body: LangRes(name))
                        }
                    }
                    Div(Class: col-md-11 mc-sm){
                        Div(Class: input-group){
                            Input(Class: form-control, Type: text, Name: Search, Value: #v_Search#)
                            Div(Class: input-group-btn){
                                Button(Body: Em(Class: fa fa-search), Class: btn btn-default, Page: roles_list, PageParams: "isSearch=1,v_Search=Val(Search)")
                            }
                        }
                    }
                }  
            }

            Div(Class: list-group-item){ 
                Table(Source: src_roles_list, Columns: "$id$=custom_id,$name$=custom_name,$type$=custom_type,$created$ / $deleted$=custom_date,$status$=custom_status,$creator$=custom_creator,$actions$=actions")
                If(GetVar(isSearch) == 1){
                    Div(Class: text-center){  
                        Button(Body: $view_all$, Class: btn btn-primary, Page: roles_list, PageParams: "isSearch=0")
                    }
                }
            }

            Div(Class: panel-footer clearfix){
                Div(Class: pull-right){
                    Button(Body: LangRes(create), Class: btn btn-primary, Page: roles_create)
                }
            }
            
        }           
    }

}

Div(Class: content-wrapper){
    
    If(#v_role_id# > 0){
        
        SetTitle($role$: #v_role_name#)
        Div(Class: breadcrumb){
            LinkPage($roles$, roles_list)
            Span(/).Style(margin-right: 10px; margin-left: 10px;)
            Span(Class: text-muted, Body: #v_role_name#)
        }
        
        DBFind(Name: roles_assign, Source: src_roles_assign).Custom(custom_id){
            If(#delete# == 0){
                Span(Body: #id#)
            }.Else{
                Span(Class: text-muted, Body: #id#)
            }
        }.Custom(custom_appointed){
            If(#delete# == 0){
                Span(Body: #appointed_by_name#)
            }.Else{
                Span(Class: text-muted, Body: #appointed_by_name#)
            }
        }.Custom(custom_role_name){
            If(#delete# == 0){
                Span(Body: LangRes(#role_name#) )
            }.Else{
                Span(Class: text-muted, Body: LangRes(#role_name#))
            }
        }.Custom(custom_member){
            If(#delete# == 0){
                LinkPage(Page: profile_view, PageParams: "v_member_id=#member_id#"){
                    If(#member_avatar# != ""){
                        Image(Src: "#member_avatar#", Class: img-circle).Style(width: 30px; border: 1px solid #5A5D63; margin-right: 10px;)
                    }
                    Span(Class: h5 text-bold, Body: #member_name#)
                }
            }.Else{
                Div(){
                    If(#member_avatar# != ""){
                        Image(Src: "#member_avatar#", Class: img-circle).Style(width: 30px; border: 1px solid #5A5D63; margin-right: 10px;)
                    }
                    Span(Class: h5 text-muted, Body: #member_name#)
                }
            }
        }.Custom(custom_address){
            If(#delete# == 0){
                Span(Body: Address(#member_id#))
            }.Else{
                Span(Class: text-muted, Body: Address(#member_id#))
            }
        }.Custom(custom_type){
            If(#delete# == 0){
                Span(Body: EcosysParam(Name: roles_types, Index: #role_type#))
            }.Else{
                Span(Class: text-muted, Body: EcosysParam(Name: roles_types, Index: #role_type#))
            }
        }.Custom(custom_date){
            If(#delete# == 0){
                Div(Class: h6 m0, Body: DateTime(DateTime: #date_start#, Format: "DD.MM.YYYY HH:MI"))
                Div(Class: h6 m0, Body: DateTime(DateTime: #date_end#, Format: "DD.MM.YYYY HH:MI"))
            }.Else{
                Div(Class: h6 m0 text-muted, Body: DateTime(DateTime: #date_start#, Format: "DD.MM.YYYY HH:MI"))
                Div(Class: h6 m0 text-muted, Body: DateTime(DateTime: #date_end#, Format: "DD.MM.YYYY HH:MI"))
            }
        }.Custom(custom_status){
            If(#delete# == 0){
                Span(Body: LangRes(active))
            }.Else{
                Span(Class: text-muted, Body: LangRes(deleted))
            }
        }.Custom(actions){
            If(#delete# == 0){
                Div(Class: text-center, Button(Body: Em(Class: fa fa-trash), Class: btn btn-danger, Contract: Roles_Unassign, Params: "row_id=#id#", Page: roles_view, PageParams: "v_role_id=#v_role_id#,v_role_name=#v_role_name#"))
            }
        }.Where("role_id='#v_role_id#'").Order("delete ASC, id ASC")
        
        Div(Class: panel panel-primary){
            Form(){
                Div(Class: list-group-item){
                    Table(Source: src_roles_assign, Columns: "$id$=custom_id,$member$=custom_member,$member_id$=custom_address,$role$=custom_role_name,$type$=custom_type,$appointed$ / $deleted$=custom_date,$appointed$=custom_appointed,$status$=custom_status,$actions$=actions")
                }
                Div(Class: panel-footer clearfix){
                    Div(Class: pull-right){
                        Button(Body: LangRes(assign), Class: btn btn-primary, Page: roles_assign, PageParams: "v_role_id=#v_role_id#,v_role_name=#v_role_name#")
                    }
                }
            }
        }
        
    }.Else{
        Div(Class: md-12 alert alert-danger text-center){
            Span(Body: LangRes(attention))
        }
    }
    
}

SetTitle($sysparams_candidate_validators_form$)
Div(content-wrapper){
    Div(row){
        Div(col-md-10 col-md-offset-1 col-lg-8 col-lg-offset-2){
            Form(panel panel-primary){
                Div(panel-body){
                    Div(form-group) {
                        Label(LangRes(host_ip)*)
                        Input(Name: HostIp, Type: text, Placeholder: xxx.xxx.xxx.xxx).Validate(minLength:7, maxLength:15)
                        InputErr(Name: HostIp, minLength: Value is too short, maxLength: Value is too long)
                    }
                    Div(form-group) {
                        Label(LangRes(commission_pub)*)
                        Input(Name: CommissionPub, Type: text).Validate(minLength:128, maxLength:130)
                        InputErr(Name: CommissionPub, minLength: Invalid pub-key, maxLength: Invalid pub-key)
                    }
                    Div(form-group) {
                        Label(LangRes(commission_address)*)
                        Input(Name: CommissionAddress, Type: text,Placeholder:xxxx-xxxx-xxxx-xxxx-xxxx).Validate(minLength:24, maxLength:24)
                        InputErr(Name: CommissionAddress, minLength: Invalid address, maxLength: Invalid address)
                    }
                    Div(form-group) {
                        Label(LangRes(description_equipment))
                        Input(Name: Description, Type: textarea)
                    }
                    Div(form-group) {
                        Label(LangRes(comment))
                        Input(Name: Comment, Type: textarea)
                    }
                }
                Div(panel-footer){
                    Button(Contract: sysparams_CandidateValidatorRequest, Class: btn btn-primary, Page: sysparams_candidate_validators_form, Body: $send$)
                }
            }
        }
    }
}

SetTitle($sysparams_candidates_list$)
SetVar(this, sysparams_candidates_list)
DBFind(Name:sysparams_candidate, Source: src_candidates).Where("deleted=0").Custom(buttons){
    Form(btn-group){
        
        Input(Name:Id, Type: hidden, Value: #id#)
        Input(Name:Start, Type: hidden, Value: Now("YYYY-MM-DD"))
        Input(Name:StartTime, Type: hidden, Value: Now("HH:MI", +1 minute))
        Input(Name:End, Type: hidden, Value: Now("YYYY-MM-DD", +14 days))
        Input(Name:EndTime, Type: hidden, Value: Now("HH:MI", +3 minute))
        Button(Body: $voting_start$, Contract: sysparams_StartVotingValidator, Page: #this#, Params: "VotingParamId=1", Class: btn btn-primary)
    }
}.Custom(_date){
    DateTime(DateTime: #date_created#, Format: YYYY-MM-DD HH:MI)
}
Div(content-wrapper){
    Div(row){
        Div(col-md-12){
            Div(panel panel-primary){
                Div(table-responsive){
                    Table(Source: src_candidates, Columns: "$host_ip$=host_ip,$commission_address$=commission_address,$description_equipment$=description,$comment$=comment,$date_created$=_date,=buttons")
                    
                }
            }
        }
    }
}

Div(Class: content-wrapper){

	SetTitle($system_params$)
	Div(breadcrumb){
		Span(Class: text-muted, Body: $system_params$)
	}

    Data(src_parameters,"id,name"){
        1,"gap_between_blocks"
        2,"default_ecosystem_page"
        3,"full_nodes"
        4,"count_of_nodes"
        5,"ecosystem_price"
        6,"blockchain_url"
        7,"max_block_size"
    }.Custom(custom_value){
        SysParam(#name#)
    }.Custom(actions){
        Button(Body: $edit$, Class: btn btn-primary, Page: sysparams_voting, PageParams: "params_id=#id#,params_name=#name#")
    }

    Div(Class: panel panel-primary){
        Form(){
            Div(Class: list-group-item){
                Table(src_parameters, "$id$=id,$name$=name,$value$=custom_value,$actions$=actions")
            }
        }           
    }
}

