contract DemoContract {
    data {
        SomeText string
    }

    conditions {
        $memberFind = DBFind("member").WhereId($key_id)
        if Len($memberFind) == 0{
            info "Member not found"
        }
        $member = $memberFind[0]
    }
    action {
        DBInsert("demo_page_table", "member_name,member_id,some_text", $member["username"], $member["id"], $SomeText)
    }
}

contract Notifications_Single_Send {
    data {
        member_id int
        icon_name string
        text_header string
        text_body string
        page_name string
        params_val string
    }

    conditions {
		$member_map = DBFind("member").Where("id = $", $member_id).Row()

		if ($member_map["id"] == nil){
			warning "Member not found"
		}
    }

    action {
		$notification_id = DBInsert(`notifications`, `icon,header_text,body_text,page_name,notification_type,recipient_id,recipient_name,recipient_avatar,timestamp date_create`, 
			$icon_name, $text_header, $text_body, $page_name, 1, $member_id, $member_map["member_name"], $member_map["avatar"], $block_time)

		var params string
		params = "notific_id=" + Str($notification_id) + ","
		params = params + "notific_type=1,"
		params = params + "notific_header=" + $text_header + ","
		params = params + $params_val

		DBUpdate(`notifications`, $notification_id, `page_params`, params)
    }
}

contract DemoProfileStore {
    data {
        Username string
        Avatar string "image"
    }

    conditions {
        $id = 0
        $memberFind = DBFind("member").WhereId($key_id)
        if Len($memberFind) > 0 {
            $member = $memberFind[0]
            $id = Int($member["id"])
        }
    }

    action {
        if $id == 0{
            DBInsert("member", "id,username,avatar", $key_id, $Username, $Avatar)
        }else{
            DBUpdate("member", $id, "username,avatar", $Username, $Avatar)
        }
    }
}

contract Notifications_Single_Close {
    data {
        notific_id int
    }
    action {
        DBUpdate(`notifications`, $notific_id, `closed,timestamp finished_processing_time,finished_processing_id`, 1, $block_time, $key_id)
    }
}

contract DemoNotificationSingle {
  data {
    MemberId int
    SomeText string
  }

  conditions {
    $icon_name = "fa-bell"
    $text_header="You got a message"
    $page_name="demo_page_sendMessage"
    $params_val= Sprintf("SomeText=%v", $SomeText)
    $text_body = "click for open"
  }

  action {
    Notifications_Single_Send("member_id,icon_name,text_header,text_body,page_name,params_val", $MemberId, $icon_name, $text_header, $text_body, $page_name, $params_val)
  }
}

