contract NewBlock {
		data {
			ApplicationId int
			Name       string
			Value      string
			Conditions string
		}
		conditions {
			ValidateCondition($Conditions,$ecosystem_id)

			var row map
			row = DBRow("blocks").Columns("id").Where("name = ?", $Name)

			if row {
				warning Sprintf( "Block %s already exists", $Name)
			}
		}
		action {
			DBInsert("blocks", "name,value,conditions,application_id", $Name, $Value, $Conditions, $ApplicationId)
		}
	}

