            	}
            	else {
            		res = res + Sprintf(`%q: %q, `,$IdLang[i],$Localiz[i])
            		i = i + 1
            	}
            }

    		res = Sprintf("{"+"%v"+"}", res)
			DBUpdateExt("languages", "name", $Name, "res", res )
			UpdateLang($Name, res)
		}
	}

