contract tokenrefund_Start{
    data{
        SufferingAccount string
        SuspectAccount string
        Amount int
        Note string
        DateStart string "date"
        DateEnd string "date"
        DatetimeNow string "date"
        TimeNow string "date"
    }
    func blockAccounts(sufferingKey, suspectKey int){
        //todo: БЛОКИРОВКА АККАУНТОВ
        Debug("Value", Sprintf("БЛОКИРОВКА АККАУНТОВ (%s, %s)", sufferingKey, suspectKey))
    }

    conditions{
        Debug("Value", "tokenrefund_Start")

        $sufferingKey = AddressToId($SufferingAccount)
        $suspectKey = AddressToId($SuspectAccount)
        if $Amount <= 0{
            info "Amount not valid"
        }
        if $sufferingKey == 0{
            info "Suffering address not valid"
        }
        if $suspectKey == 0{
            info "Suspect address not valid"
        }
        if Size($Note) == 0{
            info "Please add description"
        }
        var allowed map
        allowed = DBFind("roles_assign").Where("role_name='Validator' and role_type=3 and delete=0 and member_id=?", $key_id)
        if !allowed{
            // закомментировано для отладки
            // warning "You not Validator. You can not to run this"
        }

        var template_id int
        template_id = DBFind("laws").Where("name='tokenrefund'").One("voting_template_id")
        if template_id == nil{
            template_id = DBFind("voting_templates").Where("type='law' and type_id=1").One("id")
            if template_id == nil{
                info "Template not found"
            }
            Debug("Value", "template id найдено в voting_templates")
        }

        $template = DBFind("voting_templates").Where("id=?", template_id).Row()
        $votersRoleName = $template["voters"]
        $votingName = $template["title"]
        $typeParticipants = 3 // участников добавим по роли
        $typeDecision = 5 // запуск контракта
        $typeVoting = 2 // системное голосование
        $volume = Int($template["volume"])
        $quorum = Int($template["quorum"])
        $contractAccept = $template["contract_accept"]
        $contractReject = $template["contract_reject"]

        $desc = Sprintf("%v [#%v]", $votingName, template_id)

        var isExist map
        isExist = DBFind("voting_instances").Where("name=? and description=? and delete=0", $votingName, $desc).Row()
        if isExist{
            // info "Voting already created" // раскомментировать на проде
        }

        Debug("Value", "tokenrefund_Start. condition ok")
    }

    action{
        blockAccounts($sufferingKey,$suspectKey)

        $voting_id = 0 // должно установиться в voting_CreateNew
        voting_CreateNew("voting_name,type_voting,description,type_participants,type_decision,volume,quorum,now_date,start_time,end_time,start_date,end_date", $votingName, $typeVoting, $desc, $typeParticipants, $typeDecision, $volume, $quorum, $DatetimeNow, $TimeNow, $TimeNow, $DateStart, $DateEnd)
        if $voting_id == 0 {
            $voting_id = DBFind("voting_instances").Where("voting_name=? and start_date=?", $votingName, $DateStart).One("id")
            $voting_id = Int($voting_id)
        }
        var tokenrefundId int
        tokenrefundId = DBInsert("tokenrefund", "suffering_key_id,suspect_key_id,amount,note,validator_key_id,blocked_at,voting_id", $sufferingKey, $suspectKey, $Amount, $Note, $key_id, $DatetimeNow, $voting_id)

        $defaultParams = Sprintf("Id->%v", tokenrefundId)

        voting_SubjectContract("votingID,contract_reject,contract_accept,contract_reject_params,contract_accept_params", $voting_id, $contractReject, $contractAccept, $defaultParams, $defaultParams)

        var roleId int
        roleId = DBFind("roles_list").Where("role_name=? and delete=0", $votersRoleName).One("id")
        voting_Invite("votingID,var_id", $voting_id, roleId)


        Debug("Value", "tokenrefund_Start. action ok")
    }
}

pages.Value:
Div(content-wrapper){
    SetTitle($tokenrefund_list$)

    DBFind(Name: tokenrefund, Source: src).Order(id desc).Custom(_status){
        If(#status#>0){
            EcosysParam(Name: tokenrefund_status, Index: #status#)
        }.Else{
            $tokenrefund_status0$
        }
    }.Custom(_blocked){
        DateTime(Format: YYYY-MM-DD HH:MI:SS, DateTime: #blocked_at#)
    }.Custom(_closed){
        DateTime(Format: YYYY-MM-DD HH:MI:SS, DateTime: #closed_at#)
    }

    Div(panel panel-primary){
        Div(list-group-item){
            Table(Source: src, Columns: "$accounts$=accounts,$amount$=amount,$description$=note,$validator_id$=validator_id,$blocked$=_blocked,$voting_id$=voting_id,$result$=result,$status$=status,$closed$=_closed")
        }

        Div(panel-footer clearfix){
            Form(pull-right){
                Button(Body: $request_refund$, Class: btn btn-primary, Page: tokenrefund_start)
            }
        }
    }
}

Data(Source: inputs, Columns:"name,type,res,placeholder"){
    Amount,number,amount,""
    SufferingAccount,text,tokenrefund_suffering,xxxx-xxxx-xxxx-xxxx-xxxx
    SuspectAccount,text,tokenrefund_suspect,xxxx-xxxx-xxxx-xxxx-xxxx
    Note,textarea,description,""
}
Div(content-wrapper){
    SetTitle($tokenrefund_start$)
    Div(row){
        Div(col-sm-8 col-sm-offset-2 col-lg-6 col-lg-offset-3){
            Form(panel panel-primary){
                Div(panel-heading, Body: $new_voting_value$)
                Div(panel-body){
                    ForList(Source: inputs){
                        Div(row form-group){
                            Div(col-sm-4){LangRes(#res#)}
                            Div(col-sm-8){
                                Input(Name: #name#, Type: #type#, Placeholder: #placeholder#)
                            }
                        }
                    }
                }
                Div(panel-footer text-right){
                    Input(Name: DateStart, Type: hidden, Value: Now("YYYY-MM-DD", +1 day))
                    Input(Name: DateEnd, Type: hidden, Value: Now("YYYY-MM-DD", +2 day))
                    Input(Name: DatetimeNow, Type: hidden, Value: Now("YYYY-MM-DD HH:MI"))
                    Input(Name: TimeNow, Type: hidden, Value: Now("HH:MI"))
                    Button(Body: $start_refund$, Contract: tokenrefund_Start, Page: tokenrefund_list, Class: btn btn-primary)
                }
            }
        }
    }
}
